#!/bin/bash
sed -i '.bak' "s/.*NONCESTATISTICS_VERSION_COUNT.*/#define NONCESTATISTICS_VERSION_COUNT \"$(git rev-list --count HEAD)\"/" ./noncestatistics/all.h 2>/dev/null || sed -i "s/.*NONCESTATISTICS_VERSION_COUNT.*/#define NONCESTATISTICS_VERSION_COUNT \"$(git rev-list --count HEAD)\"/" ./noncestatistics/all.h 2>/dev/null
sed -i '.bak' "s/.*NONCESTATISTICS_VERSION_SHA.*/#define NONCESTATISTICS_VERSION_SHA \"$(git rev-parse HEAD)\"/" ./noncestatistics/all.h 2>/dev/null || sed -i "s/.*NONCESTATISTICS_VERSION_SHA.*/#define NONCESTATISTICS_VERSION_SHA \"$(git rev-parse HEAD)\"/" ./noncestatistics/all.h 2>/dev/null
